req_handler.code:
(function(inputMsg){
     var inputArr = inputMsg.byteArr;
     var handled = false;
     var outputArr = null;

     //test
     if (inputArr.getU08(0) === 0x03){
        outputArr = factory.createByteArr();
        outputArr.putU08(1, 0x04);
        handled = true;
     };

     return {
        handled: handled,
        response: outputArr
     };
 })

(function(inputMsg){
     var inputArr = inputMsg.byteArr;
     var handled = false;
     var outputArr = null;
     if (this.val === undefined){
        this.val = 0;
     }

     if (inputArr.getU08(0) === 0x04){
        outputArr = factory.createByteArr();
        outputArr
           .putU16(1, this.val++)
           .putU16(5, 0x1234, LITTLE_END);

        io.writeEncoded(outputArr);

        if ((this.val & 0x0f) == 0){
           outputArr.putU16(10, 0x1234, BIG_END);
        }
        handled = true;
     };

     return {
        handled: handled,
        response: outputArr
     };
 })

(function(inputMsg){
     var inputArr = inputMsg.byteArr;
     var handled = false;
     var outputArr = null;

     if (inputArr.getU08(0) === 0x04){
        outputArr = factory.createByteArr();
        outputArr.putU16(1, 0x1234);
        outputArr.putU16(5, 0x1234, LITTLE_END);
        outputArr.putU16(10, 0x1234, BIG_END);
        handled = true;
     };

     return {
        handled: handled,
        response: outputArr
     };
 })
