req_handler.code:
(function(inputMsg){
     var inputArr = inputMsg.byteArr;
     var handled = false;
     var outputArr = null;

     //test
     if (inputArr.getU08(0) === 0x03){
        outputArr = factory.createByteArr();
        outputArr.putU08(1, 0x04);
        handled = true;
     };

     return {
        handled: handled,
        response: outputArr
     };
 })
